import React, { useState } from 'react';
import './CalendarComponent.css';

const CalendarComponent = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedMonth, setSelectedMonth] = useState(5); 
  const [selectedYear, setSelectedYear] = useState(2024); 
  const [plans, setPlans] = useState({});

  const months = [
    'Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь',
    'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'
  ];

  const daysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const generateCalendar = () => {
    const days = [];
    const totalDays = daysInMonth(selectedMonth, selectedYear);
    for (let i = 1; i <= totalDays; i++) {
      days.push(i);
    }
    return days;
  };

  const handleDateClick = (day) => {
    setSelectedDate(day === selectedDate ? null : day);
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(parseInt(event.target.value));
    setSelectedDate(null);
  };

  const handleYearChange = (event) => {
    setSelectedYear(parseInt(event.target.value));
    setSelectedDate(null);
  };

  const addPlan = () => {
    if (selectedDate === null) {
      alert('Выберите дату, чтобы добавить план.');
      return;
    }

    const formattedDate = `${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`;

    if (plans[formattedDate] && plans[formattedDate].length >= 10) {
      alert(`Лимит планов на ${selectedDate} ${months[selectedMonth]} уже исчерпан.`);
      return;
    }

    const planText = prompt(`Введите план на ${selectedDate} ${months[selectedMonth]} ${selectedYear}:`);
    if (planText) {
      const newPlan = `${planText}`;
      const newPlans = {
        ...plans,
        [formattedDate]: plans[formattedDate] ? [...plans[formattedDate], newPlan] : [newPlan]
      };
      setPlans(newPlans);
    }
  };

  const deletePlan = (formattedDate, index) => {
    const updatedPlans = { ...plans };
    updatedPlans[formattedDate].splice(index, 1);
    setPlans(updatedPlans);
  };

  return (
    <div className="calendar-container">
      <div className="calendar">
        <div className="calendar-header">
          <select className="month-select" value={selectedMonth} onChange={handleMonthChange}>
            {months.map((month, index) => (
              <option key={index} value={index}>{month}</option>
            ))}
          </select>
          <input type="number" className="year-input" value={selectedYear} onChange={handleYearChange} />
        </div>
        <div className="days-grid">
          {['Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб', 'Вс'].map((day, index) => (
            <div key={index} className="day-name">{day}</div>
          ))}
          {generateCalendar().map((day) => {
            const formattedDate = `${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
            const hasPlans = plans[formattedDate];

            return (
              <div
                key={day}
                className={`calendar-day ${selectedDate === day ? 'selected' : ''}`}
                onClick={() => handleDateClick(day)}
              >
                {day}
                {hasPlans && <div className="plan-indicator" />}
              </div>
            );
          })}
        </div>
        {selectedDate !== null &&
          <div className="selected-date-info">
            <p>{selectedDate} {months[selectedMonth]}, {selectedYear}</p>
            {plans[`${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`] ?
              <div>
                <p>Планы на {selectedDate} {months[selectedMonth]} есть:</p>
                {plans[`${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`].map((plan, index) => (
                  <div key={index}>
                    <p>{index + 1}. {plan}</p>
                    <button onClick={() => deletePlan(`${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`, index)}>Удалить</button>
                  </div>
                ))}
              </div>
              :
              <p className="no-plans">Нет планов на {selectedDate} {months[selectedMonth]}</p>
            }
            {(!plans[`${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`] ||
              plans[`${selectedYear}-${(selectedMonth + 1).toString().padStart(2, '0')}-${selectedDate.toString().padStart(2, '0')}`].length < 10) &&
              <button onClick={addPlan}>Добавить план на {selectedDate} {months[selectedMonth]}</button>
            }
          </div>
        }
      </div>
    </div>
  );
};

export default CalendarComponent;
