import React, { useState, useEffect } from "react";
import { Link, Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import "../css/profil.css";
import authHeader from "../services/auth-header";

const UserProfile = () => {
  const [redirect, setRedirect] = useState(null);
  const [userReady, setUserReady] = useState(false);
  const [currentUser, setCurrentUser] = useState({ username: "", email: "", firstName: "", lastName: "", phone: "", avatar: "" });
  const [updatedUser, setUpdatedUser] = useState({ username: "", email: "", firstName: "", lastName: "", phone: "", avatar: "" });

  useEffect(() => {
    const fetchCurrentUser = () => {
      const currentUser = AuthService.getCurrentUser();
      if (!currentUser) {
        setRedirect("/home");
      } else {
        setCurrentUser(currentUser);
        setUpdatedUser(currentUser);
        setUserReady(true);
      }
    };

    fetchCurrentUser();

    // Clean up function to cancel any pending requests or timers
    return () => {
      // Implement any cleanup logic here if necessary
    };
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdatedUser({ ...updatedUser, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch("/api/users/profile", {
        method: "PUT",
        headers: {
          headers: authHeader()
        },
        body: JSON.stringify(updatedUser),
      });
      if (response.ok) {
        const data = await response.json();
        setCurrentUser(data);
        setUpdatedUser(data);
        console.log("Profile updated successfully");
      } else {
        console.error("Error updating user profile:", response.statusText);

      }
    } catch (error) {
      console.error("Error updating user profile:", error);
    }
  };

  if (redirect) {
    return <Navigate to={redirect} />;
  }

  return (
    <div className="container emp-profile">
        <form onSubmit={handleSubmit}>
      <form method="post">
        <div className="row">
          <div className="col-md-4">
            <div className="profile-img">
              <img
                src={updatedUser.avatar || "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS52y5aInsxSm31CvHOFHWujqUx_wWTS9iM6s7BAm21oEN_RiGoog"}
                alt=""
              />
              <div className="file btn btn-lg btn-primary">
                Change Photo
                <input type="file" name="file" />
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <div className="profile-head">
              <h5>{currentUser.username}</h5>
              <h6>Top Donators Rank:</h6>

              <ul className="nav nav-tabs" id="myTab" role="tablist">
                <li className="nav-item">
                  <a
                    className="nav-link active"
                    id="home-tab"
                    data-toggle="tab"
                    href="#home"
                    role="tab"
                    aria-controls="home"
                    aria-selected="true"
                  >
                    <Link to={"/profile"}>About</Link>
                  </a>
                </li>
                <li className="nav-item">
                  <a
                    className="nav-link"
                    id="profile-tab"
                    data-toggle="tab"
                    href="#profile"
                    role="tab"
                    aria-controls="profile"
                    aria-selected="false"
                  >
                    {/* <Link to = {'/posts/'+post.id + '/edit'}> */}
                    <Link to={"/profile/"+currentUser.id+"/edit"}>Edit profile</Link>
                  </a>
                </li>
              </ul>
            </div>
          </div>
          <div className="col-md-2">
            <input
              type="submit"
              className="profile-edit-btn"
              name="btnAddMore"
              value="Donate!"
            />
          </div>
        </div>
        <div className="row">
          <div className="col-md-4">
            <div className="profile-work"></div>
          </div>
          <div className="col-md-8">
            <div className="tab-content profile-tab" id="myTabContent">
              <div
                className="tab-pane fade show active"
                id="home"
                role="tabpanel"
                aria-labelledby="home-tab"
              >
                 <div className="row">
                  <div className="col-md-6">
                    <label>Name</label>
                  </div>
                  <div className="col-md-6">
                    <input
                      type="text"
                      name="firstName"
                      value={updatedUser.firstName}
                      onChange={handleInputChange}
                      className="black-border"      
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <label>Surname</label>
                  </div>
                  <div className="col-md-6">
                    <input
                      type="text"
                      name="lastName"
                      value={updatedUser.lastName}
                      onChange={handleInputChange}
                      className="black-border"  
                      
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <label>Phone Number </label>
                  </div>
                  <div className="col-md-6">
                    <input
                      type="text"
                      name="phone"
                      value={updatedUser.phone}
                      onChange={handleInputChange}
                      className="black-border"  
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <label>Username</label>
                  </div>
                  <div className="col-md-6">
                    <p>{currentUser.username}</p>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <label>Email</label>
                  </div>
                  <div className="col-md-6">
                    <p>{currentUser.email}</p>
                  </div>
                </div>
                {/* Add more fields for saving in the database */}
                <div className="row">
                  <div className="col-md-6">
                    <label>Token</label>
                  </div>
                  <div className="col-md-6">
                    <p>
                      {currentUser.accessToken
                        ? `${currentUser.accessToken.substring(0, 20)} ... ${
                            currentUser.accessToken.substr(
                              currentUser.accessToken.length - 20
                            )
                          }`
                        : "Access Token Not Available"}
                    </p>
                  </div>
                </div>
                <div className="row">
                <div className="col-md-6">
                  <div className="col-md-6">
                                <div className="col-md-2">
                                    <input
                                        type="submit"
                                        className="profile-edit-btn"
                                        name="btnAddMore"
                                        value="Save Changes"
                                    />
                                    
                                </div>
                                
                    </div>
                    </div>
                </div>
              </div>
              
              <div
                className="tab-pane fade"
                id="profile"
                role="tabpanel"
                aria-labelledby="profile-tab"
              ></div>
              
