import { Link, useParams } from "react-router-dom";
import { useLoaderData } from "react-router-dom";
import './index.css'

export default function AnimalItem() {
  const params = useParams();
  const animal = useLoaderData();

  // Assuming you fetch the image dynamically
  const imagePath = require(`../images/${animal.image}`);

  return (
    <div className="flex items-center justify-center">
      <div className="rounded-lg shadow-md p-8">
        <h1 className="text-3xl font-normal">{animal.name}</h1>
        <h1 className="text-lg font-normal">Price: {animal.price}</h1>
        <h1 className="text-lg font-normal">City: {animal.city}</h1>
        <img className="mt-4" src={imagePath.default} alt="Animal Image" style={{ maxWidth: "300px" }} />
        <button className="bg-indira py-2 px-4 rounded-lg mt-4">
          <Link to="/animals">Close</Link>
        </button>
      </div>
    </div>
  );
}
