import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import TokenService from '../services/token.service';
import AuthService from '../services/auth.service';

const CreatePost = () => {
    const [title, setTitle] = useState('');
    const [photo, setPhoto] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState('');

    const navigate = useNavigate();

    const username = AuthService.getCurrentUser()?.username;

    useEffect(() => {
        // Fetch categories or any other initial data if needed
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const newPost = {
            title,
            userId,
            photo,
            description,
            status: 'pending', // Always set status to 'pending'
            category: selectedCategory // Assuming you have selectedCategory state for the category selection
        };

        try {
            const token = TokenService.getLocalAccessToken(); // Retrieve access token
            await axios.post('http://localhost:8080/api/posts', newPost, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            setTitle('');
            setPhoto('');
            setDescription('');
            setError(null);
            console.log('Post created successfully');
            navigate("/blog");
        } catch (error) {
            setError(error.message);
            console.error('Error creating post:', error);
        }
    };

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-lg-6">
                    <div>
                        <h2 className="text-center mb-4" style={{fontWeight: 'bold', fontSize: '24px'}}>Create New Post</h2>

                        {error && <div className="alert alert-danger">{error}</div>}
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="title" className="form-label">Title</label>
                                <input type="text" className="form-control" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="photo" className="form-label">Photo URL</label>
                                <input type="url" className="form-control" id="photo" value={photo} onChange={(e) => setPhoto(e.target.value)} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="description" className="form-label">Description</label>
                                <textarea className="form-control" id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="status" className="form-label">Status</label>
                                <input type="text" className="form-control" id="status" value="Pending" readOnly />
                            </div>
                            <button type="submit" className="btn btn-primary">Create Post</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreatePost;
