import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import authHeader from '../services/auth-header';

const CreatePost = () => {
    const [title, setTitle] = useState('');
    const [file, setFile] = useState(null); // Updated state to store file
    const [description, setDescription] = useState('');
    const [categoryId, setCategoryId] = useState('');
    const [categories, setCategories] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/categories', {
                headers: authHeader()
            });
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories:', error.message);
        }
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const userId = JSON.parse(localStorage.getItem('user')).id;

        // Create FormData object to send data as multipart/form-data
        const formData = new FormData();
        formData.append('title', title);
        formData.append('photo', file); // Append the selected file
        formData.append('description', description);
        formData.append('userId', userId);
        formData.append('categoryId', categoryId);

        try {
            await axios.post('http://localhost:8080/api/posts', formData, {
                headers: {
                    ...authHeader(),
                    'Content-Type': 'multipart/form-data' // Set content type for FormData
                }
            });
            setTitle('');
            setFile(null);
            setDescription('');
            setError(null);
            console.log('Post created successfully');
            navigate("/user");
        } catch (error) {
            setError('Error creating post. Please try again.');
            console.error('Error creating post:', error);
        }
    };

    const handleCategoryChange = (e) => {
        setCategoryId(e.target.value);
    };

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-lg-6">
                    <div>
                        <h2 className="text-center mb-4" style={{fontWeight: 'bold', fontSize: '24px'}}>Create New Post</h2>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="title" className="form-label">Title</label>
                                <input type="text" className="form-control" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="photo" className="form-label">Photo</label>
                                <input type="file" className="form-control" id="photo" onChange={handleFileChange} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="description" className="form-label">Description</label>
                                <textarea className="form-control" id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="category" className="form-label">Category</label>
                                <select className="form-select" id="category" value={categoryId} onChange={handleCategoryChange} required>
                                    <option value="">Select Category</option>
                                    {categories.map(cat => (
                                        <option key={cat.id} value={cat.id}>{cat.name}</option>
                                    ))}
                                </select>
                            </div>
                            <button type="submit" className="btn btn-primary">Create Post</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreatePost;
