// Sidebar.js
import React from 'react';

const Sidebar = ({
    categories,
    deleteCategory,
    handleShowCategoryPosts,
    handleShowMyPosts,
    currentUser,
    handleSearch
}) => {
    return (
        <div className="col-lg-4">
            {/* Search widget */}
            <div className="card mb-4">
                <div className="card-header">Search</div>
                <div className="card-body">
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            const formData = new FormData(e.target);
                            const searchValue = formData.get('searchTerm');
                            handleSearch(searchValue);
                        }}
                    >
                        <div className="input-group">
                            <input
                                className="form-control"
                                type="text"
                                name="searchTerm"
                                placeholder="Enter search term..."
                                aria-label="Enter search term..."
                                aria-describedby="button-search"
                            />
                            <button
                                className="btn btn-primary"
                                id="button-search"
                                type="submit"
                            >
                                Go!
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            {/* Categories widget */}
            <div className="card mb-4">
                <div className="card-header">Categories</div>
                <div className="card-body">
                    <div className="row">
                        <div className="col-sm-6">
                            <ul className="list-unstyled mb-0">
                                {categories.map((category) => (
                                    <li key={category.id}>
                                        <span>{category.name}</span>
                                        <button
                                            onClick={() => deleteCategory(category.id)}
                                            className="btn btn-danger btn-sm ml-2"
                                        >
                                            Delete
                                        </button>
                                        <button
                                            onClick={() => handleShowCategoryPosts(category.id)}
                                            className="btn btn-primary btn-sm ml-2"
                                        >
                                            Show
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </div>
                        {/* Add new category */}
                        <div className="col-sm-6">
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    const formData = new FormData(e.target);
                                    const name = formData.get('name');
                                    createCategory({ name });
                                }}
                            >
                                <div className="form-group">
                                    <input
                                        type="text"
                                        name="name"
                                        className="form-control"
                                        placeholder="New category name"
                                        required
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary">
                                    Create
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
            {/* Show my posts button */}
            <button
                onClick={() => handleShowMyPosts(currentUser.id)}
                className="btn btn-primary"
            >
                Show My Posts
            </button>
            <button
                onClick={() => {
                    setSelectedCategoryId(null);
                    setUserId(null);
                    setSearchTerm('');
                }}
                className="btn btn-primary"
            >
                Show All Posts
            </button>
            {/* Side widget */}
            <div className="card mb-4">
                <div className="card-header">Side Widget</div>
                <Link to={`/post/create`} className="btn btn-primary">
                    Create Post
                </Link>
            </div>
        </div>
    );
};

export default Sidebar;
