import React, { Component } from "react";
import ReactDOM from 'react-dom';
import { RouterProvider, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AuthService from "./services/auth.service";
import Login from "./components/newLogin.js";
import Register from "./components/Register.js";
import Registration from "./components/Registration";
import Home from "./components/Home.js";
import Profile from "./components/Profile.js";
import BoardUser from "./components/boardUser.js";
import BoardModerator from "./components/boardModerator.js";
import BoardAdmin from "./components/boardAdmin.js";
import Animals from './components/Animals';
import AnimalItem from './components/AnimalItem';
import Page404 from './components/Page404';
import { getAllAnimals, getOneAnimal } from './components/api';
import CreateForm from './components/CreateForm';
import EditForm from './components/EditForm';
import CreateCategories from './components/CreateCategories';
import BlogPage from './components/Blog.js';
import OnePost from "./components/OnePost.js";
import CreatePost from "./components/CreatePost.js";
import EditPost from "./components/EditPost.js";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
  }

  logOut() {
    AuthService.logout();
    this.setState({
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    });
  }

  render() {
    const { currentUser, showModeratorBoard, showAdminBoard } = this.state;

    return (
      <RouterProvider>
        <div>
          <nav className="navbar navbar-expand navbar-dark bg-dark">
            {/* Your navigation links */}
          </nav>

          <Route
            path="/"
            element={<Home />}
          />
          <Route
            path="/animal/add"
            element={<CreateForm />}
          />
          {/* Other routes */}
        </div>
      </RouterProvider>
    );
  }
}

export default App;
