import React from "react";
import { createRoot } from "react-dom/client";
import { BrowserRouter } from "react-router-dom";
import ReactDOM from 'react-dom/client';import { createBrowserRouter } from "react-router-dom";
import { RouterProvider } from "react-router-dom";
import App from "./App";
import Login from "./components/Login";
import Register from "./components/Register";
import Registration from "./components/Registration";
import Home from "./components/Home";
import Profile from "./components/Profile";
import BoardUser from "./components/boardUser";
import BoardModerator from "./components/boardModerator";
import BoardAdmin from "./components/boardAdmin";

import Animals from './components/Animals';
import AnimalItem from './components/AnimalItem';
import Page404 from './components/Page404';
import { getAllAnimals, getOneAnimal } from './components/api';
import CreateForm from './components/CreateForm';
import EditForm from './components/EditForm';
import CreateCategories from './components/CreateCategories';

import BlogPage from './components/Blog.js';
import OnePost from "./components/OnePost.js";
import CreatePost from "./components/CreatePost.js";
import EditPost from "./components/EditPost.js";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const root = ReactDOM.createRoot(document.getElementById('root'));

const routes = createBrowserRouter([
  {
    path: "/",
    element: <Home />
  },
  {
    path: "/",
    element: <CreateForm />
  },
  {
    path: "/animal/add",
    element: <CreateForm />
  },
  {
    path: "/categories",
    element: <CreateCategories />
  },
  {
    path: "/categories/create",
    element: <CreateCategories />
  },
  {
    path: "/animals/category/:catId?",
    element: <Animals />
  },
  {
    path: "/animals/",
    element: <Animals />,
    loader: getAllAnimals,
    children: [
      {
        path: "/animals/:animalId",
        element: <AnimalItem />,
        loader: async ({ params }) => getOneAnimal(params.animalId)
      }
    ]
  },
  {
    path: "/animals/:animalId",
    element: <AnimalItem />
  },
  {
    path: "/animals/:animalId/edit",
    element: <EditForm />
  },
  {
    path: "/animal-item",
    element: <AnimalItem />
  },
  {
    path: "/login",
    element: <Login />
  },
  {
    path: "/register",
    element: <Register />
  },
  {
    path: "/registration",
    element: <Registration />
  },
  {
    path: "/user",
    element: <BoardUser />
  },
  {
    path: "/mod",
    element: <BoardModerator />
  },
  {
    path: "/admin",
    element: <BoardAdmin />
  },
  {
    path: "/blog",
    element: <BlogPage />
  },
  {
    path: "/posts/:postId",
    element: <OnePost />
  },
  {
    path: "/post/create",
    element: <CreatePost />
  },
  {
    path: "/posts/:postId/edit",
    element: <EditPost />
  },
  {
    path: "*",
    element: <Page404 />
  }
]
);

root.render(
  <RouterProvider router={routes} />
);
//npx json-server -p 4000 -w ./data/db.json
