import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import authHeader from '../services/auth-header';
import AuthService from "../services/auth.service";
import Sidebar from './Sidebar';

export default function OnePost() {
    const [post, setPost] = useState(null);
    const { postId } = useParams();
    const navigate = useNavigate();
    const currentUser = AuthService.getCurrentUser();
    const [isAuthorOrModerator, setIsAuthorOrModerator] = useState(false);

    useEffect(() => {
        fetchPost(postId); 
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const response = await fetch(`http://localhost:8080/api/posts/${id}`, {
                headers: authHeader()
            });
            if (response.ok) {
                const postData = await response.json();
                setPost(postData);
                // Check if the current user is the author or a moderator
                if (postData.user_id === currentUser.id || currentUser.role === 'moderator') {
                    setIsAuthorOrModerator(true);
                }
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    const handleDelete = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/posts/${postId}`, {
                method: 'DELETE',
                headers: authHeader()
            });
            if (response.ok) {
                console.log('Post deleted successfully');
                navigate('/blog');
            } else {
                console.error('Failed to delete post:', response.statusText);
            }
        } catch (error) {
            console.error('Error deleting post:', error.message);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            {/* Page content */}
            <div className="container">
                <div className="row">
                    {/* Blog entries */}
                    <div className="col-lg-8">
                        {/* Nested row for non-featured blog posts */}
                        <div className="row">
                            {posts.length === 0 ? (
                                <div className="col-lg-12">
                                    <p>No posts yet</p>
                                </div>
                            ) : (
                                posts.map(post => (
                                    <div className="col-lg-6" key={post.id}>
                                        {/* Blog post */}
                                        <div className="card mb-4">
                                            <img className="card-img-top" src={post.photo} alt="Post" />
                                            <div className="card-body">
                                                <div className="small text-muted">{post.createdAt}</div>
                                                <h2 className="card-title h4">{post.title}</h2>
                                                <p className="card-text">{post.description}</p>
                                                <Link to={`/posts/${post.id}`} className="btn btn-primary">Read more →</Link>
                                            </div>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                    {/* Side widgets */}
                    <Sidebar
                        categories={categories}
                        deleteCategory={deleteCategory}
                        handleShowCategoryPosts={handleShowCategoryPosts}
                        handleShowMyPosts={handleShowMyPosts}
                        currentUser={currentUser}
                        handleSearch={handleSearch}
                    />
                </div>
            </div>
            {/* Pagination */}
            <nav aria-label="Pagination">
                <hr className="my-0" />
                <ul className="pagination justify-content-center my-4">
                    <li className="page-item disabled"><a className="page-link" href="#" tabIndex="-1" aria-disabled="true">Newer</a></li>
                    <li className="page-item active" aria-current="page"><a className="page-link" href="#!">1</a></li>
                    <li className="page-item"><a className="page-link" href="#!">2</a></li>
                    <li className="page-item"><a className="page-link" href="#!">3</a></li>
                    <li className="page-item disabled"><a className="page-link" href="#!">...</a></li>
                    <li className="page-item"><a className="page-link" href="#!">15</a></li>
                    <li className="page-item"><a className="page-link" href="#!">Older</a></li>
                </ul>
            </nav>
        </div>
    );
}
