import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TokenService from '../services/token.service';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

export default function OnePost() {
    const [post, setPost] = useState(null);
    const [categories, setCategories] = useState([]);
    const [newCategory, setNewCategory] = useState('');
    const { postId } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        fetchPost(postId); 
        fetchCategories();
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/posts/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const data = await response.json();
                setPost(data);
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    const fetchCategories = async () => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch('http://localhost:8080/api/categories', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const data = await response.json();
                setCategories(data);
            } else {
                console.error('Failed to fetch categories:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching categories:', error.message);
        }
    };

    const handleDelete = async (categoryId) => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/categories/${categoryId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                console.log('Category deleted successfully');
                // Refresh categories
                fetchCategories();
            } else {
                console.error('Failed to delete category:', response.statusText);
            }
        } catch (error) {
            console.error('Error deleting category:', error.message);
        }
    };

    const handleCategorySubmit = async (e) => {
        e.preventDefault();
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch('http://localhost:8080/api/categories', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name: newCategory })
            });
            if (response.ok) {
                console.log('Category created successfully');
                // Refresh categories
                fetchCategories();
                // Clear the input field
                setNewCategory('');
            } else {
                console.error('Failed to create category:', response.statusText);
            }
        } catch (error) {
            console.error('Error creating category:', error.message);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return(
        <div className="container mt-5">
            <div className="row">
                <div className="col-lg-8">
                    {/* <!-- Post content--> */}
                    <article>
                        {/* <!-- Post header--> */}
                        <header className="mb-4">
                            {/* <!-- Post title--> */}
                            <h1 className="fw-bolder mb-1">{post.title}</h1>
                            {/* <!-- Post meta content--> */}
                            <div className="text-muted fst-italic mb-2">{post.createdAt}</div>
                            {/* <!-- Post categories--> */}
                            {post.categories.map(category => (
                                <Link key={category.id} to={`/categories/${category.id}`} className="badge bg-secondary text-decoration-none link-light me-1">{category.name}</Link>
                            ))}
                            <Link to={`/categories/${category.id}`} className="badge bg-secondary text-decoration-none link-light">{post.type}</Link>
                        </header>
                        {/* <!-- Preview image figure--> */}
                        <figure className="mb-4"><img className="img-fluid rounded" src={post.photo} alt="..." /></figure>
                        {/* <!-- Post content--> */}
                        <section className="mb-5">
                            <p className="fs-5 mb-4">{post.description}</p>
                            <p className="fs-5 mb-4">Author: {post.userId}</p>
                            <button className="btn btn-primary me-2"><Link to={`/posts/${post.id}/edit`}>Edit</Link></button>
                            <button className="btn btn-danger" onClick={handleDelete(post.id)}>Delete</button>
                        </section>
                    </article>
                    {/* <!-- Comments section--> */}
                    <section className="mb-5">
                        <div className="card bg-light">
                            <div className="card-body">
                                {/* <!-- Comment form--> */}
                                <form className="mb-4"><textarea className="form-control" rows="3" placeholder="Join the discussion and leave a comment!"></textarea></form>
                                {/* <!-- Comment with nested comments--> */}
                                <div className="d-flex mb-4">
                                    {/* <!-- Parent comment--> */}
                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                    <div className="ms-3">
                                        <div className="fw-bold">Commenter Name</div>
                                        If you're going to lead a space frontier, it has to be government; it'll never be private enterprise. Because the space frontier is dangerous, and it's expensive, and it has unquantified risks.
                                        {/* <!-- Child comment 1--> */}
                                        <div className="d-flex mt-4">
                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                            <div className="ms-3">
                                                <div className="fw-bold">Commenter Name</div>
                                                And under those conditions, you cannot establish a capital-market evaluation of that enterprise. You can't get investors.
                                            </div>
                                        </div>
