                    <div className="card mb-4">
                        <div className="card-header">Categories</div>
                        <div className="card-body">
                            <ul className="list-unstyled mb-0">
                            {categories.map(category => (
    <Link key={category.id} to={`/categories/${category.id}`} className="badge bg-secondary text-decoration-none link-light me-1">{category.name}</Link>
))}
<Link to={`/categories/${post.type}`} className="badge bg-secondary text-decoration-none link-light">{post.type}</Link>

                            </ul>
