import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createCat } from './api';
import AuthService from '../services/auth.service';

export default function CreateCategories() {
    const navigate = useNavigate();
    const [cat, setCat] = useState({
        name: '',
    });

    const handleName = (ev) => {
        setCat({ ...cat, name: ev.target.value });
    };

    const handleSubmit = async (ev) => {
        ev.preventDefault();
        const savedCat = await createCat(cat);
        navigate('/animals', {
            state: { message: savedCat.name + ' saved ', title: 'Saved cat' },
        });
    };

    // Assuming currentUser is obtained from AuthService or similar
    const currentUser = AuthService.getCurrentUser();

    // Check if currentUser exists and has the 'ROLE_MODERATOR' role
    if (!currentUser || !currentUser.roles.includes('ROLE_MODERATOR')) {
        return <div>You are not authorized to access this page.</div>;
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="max-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
                    <div className="max-w-md w-full space-y-8">
                        <div>
                            <h2 className="text-2xl text-black mb-4">Name</h2>
                            <input
                                type="text"
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm"
                                placeholder="Name"
                                value={cat.name}
                                onChange={handleName}
                            />
                        </div>

                        <button className="bg-indira text-white font-semibold py-2 px-4 rounded-lg" type="submit">
                            Save
                        </button>
                    </div>
                </div>
            </form>
        </div>
    );
}
