// Map.js
import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';

const Map = () => {
  const [chipNumber, setChipNumber] = useState('');
  const [animalLocation, setAnimalLocation] = useState(null);
  const [mapPosition, setMapPosition] = useState({ lat: 51.509865, lng: -0.118092 }); // Default map position

  const handleChipNumberChange = (event) => {
    setChipNumber(event.target.value);
  };

  const handleFindAnimal = () => {
    axios.get(`API_URL/${chipNumber}`) // Replace API_URL with your backend API endpoint
      .then(response => {
        const { lat, lng } = response.data.location; // Assuming your API returns location in lat/lng format
        setAnimalLocation({ lat, lng });
        setMapPosition({ lat, lng });
      })
      .catch(error => {
        console.error('Error retrieving animal location:', error);
        // Handle error, e.g., show an alert
      });
  };

  const mapStyles = {
    height: "400px",
    width: "100%",
  };

  const mapOptions = {
    disableDefaultUI: true,
  };

  return (
    <div className="map-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Введите номер чипа животного"
          value={chipNumber}
          onChange={handleChipNumberChange}
        />
        <button onClick={handleFindAnimal}>Найти</button>
      </div>
      <div style={mapStyles}>
        <LoadScript googleMapsApiKey="YOUR_API_KEY"> {/* Replace YOUR_API_KEY with your actual API key */}
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={10}
            center={mapPosition}
            options={mapOptions}
          >
            {animalLocation && <Marker position={animalLocation} />}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default Map;
