import React, { Component } from "react";
import { isEmail } from "validator";
import animalsImage from '../images/animals.png';
import AuthService from "../services/auth.service";

class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      email: "",
      password: "",
      confirmPassword: "",
      error: "",
      successMessage: "",
      loading: false
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { username, email, password, confirmPassword } = this.state;

    // Validation checks
    if (!username || !email || !password || !confirmPassword) {
      this.setState({ error: "All fields are required" });
      return;
    }

    if (!isEmail(email)) {
      this.setState({ error: "Invalid email format" });
      return;
    }

    if (password !== confirmPassword) {
      this.setState({ error: "Passwords do not match" });
      return;
    }

    this.setState({ loading: true });

    // Make API call to register user
    AuthService.register(username, email, password, confirmPassword)
      .then((response) => {
        this.setState({
          successMessage: response.data.message,
          error: "",
          loading: false
        });
      })
      .catch((error) => {
        this.setState({ error: error.response.data.message, loading: false });
      });
  };

  render() {
    const { username, email, password, confirmPassword, error, successMessage, loading } = this.state;

    return (
      <div className="col-md-12">
        <div className="card card-container">
          <img src={animalsImage} alt="Animals" />

          {!successMessage && (
            <form onSubmit={this.handleSubmit}>
              <div className="form-group">
                <label htmlFor="username">Username</label>
                <input
                  type="text"
                  name="username"
                  value={username}
                  onChange={this.handleChange}
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  name="email"
                  value={email}
                  onChange={this.handleChange}
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  name="password"
                  value={password}
                  onChange={this.handleChange}
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="confirmPassword">Confirm Password</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={confirmPassword}
                  onChange={this.handleChange}
                  className="form-control"
                />
              </div>

              {error && <div style={{ color: 'red' }}>{error}</div>}
              {loading ? <div>Loading...</div> : <button className="btn btn-primary btn-block">Sign Up</button>}
            </form>
          )}

          {successMessage && <div style={{ color: 'green' }}>{successMessage}</div>}
        </div>
      </div>
    );
  }
}

export default RegistrationForm;
